mapper.delete:
DELETE
  FROM S_Cardsales_B0011_Dtl
 WHERE 1 = 1
   AND CompCd = #{compCd}
   AND TrDt BETWEEN #{fromDt} AND #{toDt}

DELETE
  FROM S_Cardsales_B0031_Dtl
 WHERE 1 = 1
   AND CompCd = #{compCd}
   AND PayDt BETWEEN #{fromDt} AND #{toDt}

mapper.select:
SELECT CompCd, PayDt, SalesSumAmt, SalesSumCnt, PaySumAmt
     , Rmk, ReqCd, RegId, RegDt
  FROM S_Cardsales_B0031_Sum
 WHERE 1 = 1
   AND CompCd = #{compCd}
   AND ReqCd = #{reqCd}
